import React, { useState } from "react";

function App() {
  const [todo, setTodo] = useState(""); 
  const [todos, setTodos] = useState([]); 

  const handleAdd = () => {
    if (todo.trim() === "") return; 
    setTodos([...todos, todo]); 
    setTodo("");
  };

  return (
    <div className="todo-app">
      <h1>Todo App</h1>
      <input
        type="text"
        value={todo}
        onChange={(e) => setTodo(e.target.value)}
        placeholder="Add todo"
      />
      <button onClick={handleAdd}>Add</button>

      {/* Todo List */}
      <ul>
        {todos.map((t, index) => (
          <li key={index}>{t}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
